import React, { useState } from 'react';
import BlogContext from './BlogContext';

const BlogStates = (props) => {
  const oblogInitial = [];
  const [oblog, setOblog] = useState(oblogInitial);
  const host = 'http://localhost:3001';

  const addOblog = async (title, textbody) => {
    try {
      const response = await fetch(`${host}/api/blogs/addblog`, {
        method: 'POST',
        headers: {
          'auth-token': localStorage.getItem('token'),
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ title, textbody }),
      });

      if (!response.ok) {
        // Check if the response has an error status code
        const errorMessage = await response.json();
        throw new Error(errorMessage.error);
      }

      const newoblog = await response.json();
      setOblog(oblog.concat(newoblog));
    } catch (error) {
      console.error('Error adding blog:', error.message);
      // Handle the error or show an error message to the user
    }
  };

  return (
    <BlogContext.Provider value={{ oblog, addOblog }}>
      {props.children}
    </BlogContext.Provider>
  );
};

export default BlogStates;
